import LineGradient from "../components/LineGradient";
import { useForm } from "react-hook-form";
import { motion } from "framer-motion";

const Contact = () => {
  const {
    register,
    trigger,
    formState: { errors },
  } = useForm();

 const onSubmit = async (e) => {
   const isValid = await trigger();
    if (!isValid) {
      e.preventDefault();
    }
  };
  return (
    <section id="contact" className="py-48">
      {/* HEADINGS */}
      <motion.div
      className="flex justify-end w-full pr-15"
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true, amount: 0.5 }}
        transition={{duration: 0.5 }}
        variants={{
          hidden: { opacity: 0, x: -50 },
          visible: { opacity: 1, x: 0 },
        }}
      >
        <div>
          <p className="sm: justify-center items-centerfont-playfair font-semibold text-4xl">
            Get in<span className="text-yellow">touch </span>
          </p>
          <div className="flex md:justify-end my-5">
          <LineGradient width="mx-auto w-2/4" />
          </div>
        </div>
      </motion.div>
      {/* FORM AND IMAGE  */}
     <div className="md:flex md:justify-between gap-16 mt-5">
       <motion.div
        className="basis-1/2 flex justify-center"
         initial="hidden"
         whileInView="visible"
         viewport={{ once: true, amount: 0.5 }}
         transition={{duration: 0.5 }}
         variants={{
           hidden: { opacity: 0, y: 50 },
           visible: { opacity: 1, y: 0 },
         }}
       >
        <img src="../assets/contact-image.jpeg" alt="contact"/>
       </motion.div>
       <motion.div
       className="basis-1/2 mt-10 md:mt-0"
               initial="hidden"
               whileInView="visible"
               viewport={{ once: true, amount: 0.5 }}
               transition={{ delay: 0.2, duration: 0.5 }}
               variants={{
                 hidden: { opacity: 0, y: 50 },
                 visible: { opacity: 1, y: 0 },
               }}>
                
             {/* FORM SUBMIT Inlocuiesc key action="https://formsubmit.co/alexandrupasat1992@gmail.com"  */}
        <form target="_blank" onSubmit={onSubmit} action="https://formsubmit.co/944c70c6f0bb63a589df96f54c5d2a2f"  method="POST">
            <input 
             className="w-full bg-blue font-semibold placeholder-opaque-black p-3"
             type="text"
             placeholder="NAME"
             {...register ("name", {
                required:true,
                maxLength:100,
             })}
            />
            {errors.name && (
                <p className="text-red mt-1">
                    {errors.name.type === 'required' && 'Thus field is required'}
                    {errors.name.type === 'maxLength' && 'Max lenght is 100 characters, you have exceeded.'}

                </p>
            )}
            <input 
                   className="w-full bg-blue font-semibold placeholder-opaque-black p-3 mt-5"
                   type="text"
                   placeholder="EMAIL"
                   {...register ("email", {
                      required:true,
                      // pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                      pattern:"^[A-Z0-9_!#$%&'*+/=?`{|}~^.-]+@[A-Z0-9.-]+$"
                   })}
            />
           {errors.email && (
                <p className="text-red mt-1">
                    {errors.email.type === 'required' && 'Thus field is required'}
                    {errors.email.type === 'pattern' && 'Invalid Email Address'}
                </p>
            )}

            {/*  SECOND INPUT  */}
            <textarea 
                   className="w-full bg-blue font-semibold placeholder-opaque-black p-3 mt-5"
                   type="text"
                   placeholder="MESAGE"
                   rows="4"
                   cols="50"
                   {...register ("message", {
                      required:true,
                      maxLength:2000
                   })}
            />
           {errors.email && (
                <p className="text-red mt-1">
                    {errors.message.type === 'required' && 'Thus field is required'}
                    {errors.message.type === 'maxLenght' && 'Max lenght is 2000 characters'}
                </p>
            )}
            <button
            type="submit"
            className="p-5 bg-yellow font-semibold text-deep-blue mt-5 hover:bg-red hover:text-white transition duration-500"
            >SEND ME A MESSAGE

            </button>
        </form>
       </motion.div>
     </div>
    </section>
  );
};

export default Contact;
