import LineGradient from "../components/LineGradient";
import { motion } from "framer-motion";
// Transition for each Project Item
const container = {
  hidden: {},
  visible: {
    transition: { staggerChildren: 0.2 },
  },
};
const projectVariant = {
    hidden: {opacity:0, scale: 0.8},
    visible:{opacity:1, scale:1}
}

const Project = (title) => {
    const projectTitle = title.split("").join("-").toLowerCase()
}
const Projects = () => {
  return (
    <section id="projects" className="pt-48 pb-48">
      {/* Headings */}
      <motion.div
        className="md:w-2/4 mx-auto text-center"
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true, amount: 0.5 }}
        transition={{ delay: 0.4, duration: 0.5 }}
        variants={{
          hidden: { opacity: 0, y: -50 },
          visible: { opacity: 1, y: 0 },
        }}
      >
        <div>
          <p className=" font-playfair font-semibold text-4xl">
            My<span className="text-red">PRO</span>JECTS
          </p>
          <div className="flex justify-center mt-5">
            <LineGradient width="w-1/3" />
          </div>
        </div>
        <p className="mt-10 mb-10">
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Consectetur
          natus quidem sit similique voluptatem dolorum, modi sunt, quibusdam
          maxime, necessitatibus iste labore deleniti minus eveniet
          reprehenderit molestiae voluptatibus explicabo. Odio!
        </p>
      </motion.div>

      {/* PROJECTS */}
      <div className="flex justify-center">
        <motion.div
          className="sm:grid sm:grid-cols-3"
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.5 }}
          variants={container}
        >
            {/* Row 1 */}
              <div
               className="flex justify-center text-center items-center p-10 bg-red max-w-[400px] max-h-[400px] text-2xl font-playfair font-semibold"
              >
                USER INTERFACE
              </div>
<Project title="Project 1" />
        </motion.div>
      </div>
    </section>
  );
};
export default Projects;
